import { connect } from "react-redux";
import React, { Component } from "react";
import { filteredProducts, sortProducts } from "../actions/productActions";

// this.props.filteredProducts is all products that comes from the server for sort product
// e.target.value is the "order" lowest or highest
class Filter extends Component {
  render() {
    return this.props.filterProducts ? (
      <div>Loading...</div>
    ) : (
      <div className="filter">
        <div className="filter-result">
          {this.props.filterProducts.length} Products
        </div>
        <div className="filter-sort">
          Order{" "}
          <select
            value={this.props.sort}
            onChange={(e) =>
              this.props.sortProducts(
                this.props.filteredProducts,
                e.target.value
              )
            }
          >
            <option>Latest</option>
            <option value="lowest"> Lowest</option>
            <option value="highest"> Highest</option>
          </select>
        </div>
        <div className="filter-size">
          Filter{" "}
          <select
            value={this.props.size}
            onChange={(e) =>
              this.props.filterProducts(this.props.Products, e.target.value)
            }
          >
            <option value="">ALL</option>
            <option value="XS">XS</option>
            <option value="S">S</option>
            <option value="M">M</option>
            <option value="L">L</option>
            <option value="XL">XL</option>
            <option value="XXL">XXL</option>
          </select>
        </div>
      </div>
    );
  }
}

export default connect(
  ((state) => ({
    size: state.products.size,
    sort: state.products.sort,
    products: state.products.items,
    filteredProducts: state.products.filteredItems,
  }),
  {
    filteredProducts,
    sortProducts,
  })
)(Filter);
